-- Spread out test service locations with random offsets within each city
-- This adds small random variations to latitude and longitude so pins don't overlap

-- Riyadh profiles - spread around 24.7136, 46.6753
UPDATE profiles SET 
    service_location_lat = 24.7136 + (random() * 0.1 - 0.05),
    service_location_lng = 46.6753 + (random() * 0.1 - 0.05)
WHERE id IN (
    '10000000-0000-0000-0000-000000000001',
    '10000000-0000-0000-0000-000000000002',
    '10000000-0000-0000-0000-000000000003',
    '10000000-0000-0000-0000-000000000004',
    '10000000-0000-0000-0000-000000000005',
    '10000000-0000-0000-0000-000000000006',
    '10000000-0000-0000-0000-000000000007',
    '10000000-0000-0000-0000-000000000008',
    '10000000-0000-0000-0000-000000000009',
    '10000000-0000-0000-0000-000000000010'
);

